package edunhnil.project.forum.api.dto.loginDTO;import io.swagger.v3.oas.annotations.media.Schema;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.validation.constraints.NotBlank;import javax.validation.constraints.NotEmpty;import javax.validation.constraints.NotNull;@Data@AllArgsConstructor@NoArgsConstructorpublic class LoginRequest {    @Schema(type = "string", example = " ")    @NotEmpty(message = "username is required")    @NotBlank(message = "username is required")    @NotNull(message = "username is required")    private String username;    @Schema(type = "string", example = " ")    @NotNull(message = "password is required")    @NotEmpty(message = "password is required")    @NotBlank(message = "password is required")    private String password;}